import React, { useState } from "react";
import "./styles.css";

const emojiDictionary = {
  "üòÄ": "Grinning Face",
  "üòÉ": "Grinning Face with Big Eyes",
  "üòÑ": "Grinning Face with Smiling Eyes",
  "üòÅ": "Beaming Face with Smiling Eyes",
  "üòÜ": "Grinning Squinting Face",
  "üòÖ": "Grinning Face with Sweat",
  "ü§£": "Rolling on the Floor Laughing",
  "üòÇ": "Face with Tears of Joy",
  "üôÇ": "Slightly Smiling Face",
  "üôÉ": "Upside-Down Face",
  "üòâ": "Winking Face",
  "üòä": "Smiling Face with Smiling Eyes",
  "üòá": "Smiling Face with Halo",
  "ü•∞": "Smiling Face with Hearts",
  "üòç": "Smiling Face with Heart-Eyes",
  "ü§©": "Star-Struck",
  "üòã": "Face Savoring Food",
  "üòõ": "Face with Tongue",
  "üòú": "Winking Face with Tongue",
  "ü§™": "Zany Face",
  "üòù": "Squinting Face with Tongue",
  "ü§ë": "Money-Mouth Face",
  "ü§ó": "Hugging Face",
  "ü§≠": "Face with Hand Over Mouth",
  "ü§´": "Shushing Face",
  "ü§î": "Thinking Face",
  "ü§®": "Face with Raised Eyebrow",
  "üòê": "Neutral Face",
  "üòë": "Expressionless Face",
  "üò∂": "Face Without Mouth",
  "üòè": "Smirking Face",
  "üòí": "Unamused Face",
  "üôÑ": "Face with Rolling Eyes",
  "üò¨": "Grimacing Face",
  "ü§•": "Lying Face",
  "üòå": "Relieved Face",
  "üòî": "Pensive Face",
  "üò™": "Sleepy Face",
  "ü§§": "Drooling Face",
  "üò¥": "Sleeping Face",
  "üò∑": "Face with Medical Mask",
  "ü§í": "Face with Thermometer",
  "ü§ï": "Face with Head-Bandage",
  "ü§¢": "Nauseated Face",
  "ü§Æ": "Face Vomiting",
  "ü•µ": "Hot Face",
  "ü•∂": "Cold Face",
  "ü•¥": "Woozy Face",
  "üòµ": "Dizzy Face",
  "ü§Ø": "Exploding Head",
  "ü§†": "Cowboy Hat Face",
  "ü•≥": "Partying Face",
  "ü•∏": "Disguised Face",
  "üòà": "Smiling Face with Horns",
  "üëø": "Angry Face with Horns",
  "üëª": "Ghost",
  "üéÉ": "Jack-O-Lantern"
};

const emojis = Object.keys(emojiDictionary);

export default function App() {
  const [emoji, setEmoji] = useState("");
  // const [meaning, setMeaning] = useState("Name of the emoji will appear here");
  const [selectedEmoji, setSelectedEmoji] = useState("");

  function changeHandler(event) {
    const inputEmoji = event.target.value;
    setEmoji(inputEmoji);

    if (inputEmoji in emojiDictionary) {
      setMeaning(emojiDictionary[inputEmoji]);
      setSelectedEmoji("");
    } else {
      setMeaning("can't recognise this emoji , pls choose another one");
      setSelectedEmoji("");
    }
  }

  // function emojiClickHandler(inputEmoji) {
  //   setMeaning(emojiDictionary[inputEmoji]);
  //   setSelectedEmoji(inputEmoji);
  // }

  return (
    <div className="App">
      <h1>Pick your emoji</h1>
      <input
        onChange={changeHandler}
        value={emoji}
        placeholder={"Search your emoji"}
        style={{
          padding: "1em",
          minWidth: "80%"
        }}
      />
      <h2>{emoji}</h2>
      <h3>{meaning}</h3>
      <select
        value={selectedEmoji}
        style={{ fontSize: "1rem", padding: "0.5rem", minWidth: "30%", height: "2.5rem" }}
        onChange={(event) => {
          setSelectedEmoji(event.target.value);
          setMeaning(emojiDictionary[event.target.value]);
        }}
      >
        <option value="">Select an emoji</option>
        {emojis.map((emoji) => (
          <option value={emoji}>{emoji}</option>
        ))}
      </select>
      {selectedEmoji && (
        <div>
          <h4>{selectedEmoji}</h4>
          <h5>{emojiDictionary[selectedEmoji]}</h5>
        </div>
      )}


      {/* {emojis.map((emoji) => (
        <span
          onClick={() => emojiClickHandler(emoji)}
          style={{ fontSize: "2rem", padding: "0.5rem", cursor: "pointer" }}
        >
          {" "}
          {emoji}{" "}
        </span>
      ))} */}

      
    </div>
  );
}
